do_if.value:
@global.$kAAITByShipId.{'$' + @this.assignedcontrolled.idcode}.$attackData.$isIgnoreKAAIT and
(
	(
		@global.$kAAITByShipId.{'$' + @this.assignedcontrolled.idcode}.$attackData.$defensibleTarget.isclass.station and
		this.assignedcontrolled.shieldpercentage gt global.$kAAITByShipId.{'$' + @this.assignedcontrolled.idcode}.$attackData.$skill_shieldPercentage_withdrawAt_vStation
	) or
	(
		@global.$kAAITByShipId.{'$' + @this.assignedcontrolled.idcode}.$attackData.$defensibleTarget.isclass.ship and
		(
			this.assignedcontrolled.shieldpercentage / (@global.$kAAITByShipId.{'$' + @this.assignedcontrolled.idcode}.$attackData.$defensibleTarget.shieldpercentage)f gt (@global.$kAAITByShipId.{'$' + @this.assignedcontrolled.idcode}.$attackData.$skill_shieldPercentage_withdrawAt_vShip / 100.0) or
			@this.assignedcontrolled.bboxdistanceto.{global.$kAAITByShipId.{'$' + @this.assignedcontrolled.idcode}.$attackData.$defensibleTarget} gt this.assignedcontrolled.maxradarrange * 0.25 or
			(
				this.assignedcontrolled.shieldpercentage le 5 and
				this.assignedcontrolled.hullpercentage le 50
			)
		)
	)
)

set_value.exact:
if (not this.assignedcontrolled.isunit) and (not this.assignedcontrolled.islasertower) and
(
	(
		this.assignedcontrolled.isplayerowned and
		(
