do_if.value:
this.sector and
$kAAIT_currentOrder.exists and
(not @$kAAIT_currentOrder.$kAAITParam_iskAAITOrder) and
(
	@global.$kAAITByShipId.{'$' + this.assignedcontrolled.idcode}.$destination.$order != $kAAIT_currentOrder or
	@global.$kAAITByShipId.{'$' + this.assignedcontrolled.idcode}.$destination.$sector != this.sector
)

(
	(not @global.$kAAITByShipId.{'$' + @this.assignedcontrolled.idcode}.$attackData.$attackOrder_smallTarget.exists) or
	(not @global.$kAAITByShipId.{'$' + @this.assignedcontrolled.idcode}.$attackData.$attackOrder_smallTarget.$primarytarget.isoperational)
) and
(
	(
		@this.assignedcontrolled.order.script == 'order.fight.attack.object' and
		this.assignedcontrolled.order.$allowothertargets
	) or
	(
		@this.assignedcontrolled.commander.order.script == 'order.fight.attack.object' and
		@this.assignedcontrolled.commander.order.$allowothertargets
	) or
	(
		@this.assignedcontrolled.commander.nextorder.script == 'order.fight.attack.object' and
		@this.assignedcontrolled.commander.nextorder.$allowothertargets
	)
)
