	@this.$kAAIT_data.$defensibleTarget.isoperational and
	$kAAIT_bigEnemy_inList.bboxdistanceto.{this.$kAAIT_data.$defensibleTarget} le this.assignedcontrolled.maxradarrange * 0.5
